package OOP_Skill_2;

public class char_to_str {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch[] = {'s','a','i','s','r','e','e'};
		String s1 = "saisree";
		String s2 = new String(ch);
		System.out.println(s1);
		System.out.println(s2);

	}

}
